import React, { useState } from "react";
import { products } from "../../data";

import "./search.css";

const Search = () => {
  const [filter, setFilter] = useState("");

  const seachText = (e) => {
    setFilter(e.target.value);
  };

  let dataSeach = products.cardData.filter((item) => {
    return Object.keys(item).some(key=>item[key].toString().tolowerCase().include(filter.toString().tolowerCase()))
  });

//   console.warn(filter);
  return (
    <div>
      <input
        className="input"
        name="text"
        // defaultValue={""}
        placeholder="Search..."
        type="search"
        value={filter}
        onChange={seachText.bind(this)}
      ></input>
    </div>
  );
};

export default Search;
